  //     console.log(res.data.data);
  //   } catch (err) {
  //     console.log("Can't load the items");
  //   }
  // };
  getvendors = async () => {
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
    };

    try {
      const res = await axios.get(
        `http://localhost:5000/api/v1/users/fundriser`,
        config
      );
      this.setState({
        fundrisers: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  getusers = async () => {
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
    };

    try {
      const res1 = await axios.get(
        `http://localhost:5000/api/v1/users/user`,
        config
      );
      this.setState({
        users: res1.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  getorders = async () => {
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
    };

    try {
      const res = await axios.get(
        `http://localhost:5000/api/v1/orders/orderd`,
        config
      );
      this.setState({
        orders: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  render() {
    return (
      <Router>
        <div>
          <div className="container-scroller">
            <NavbarT />

            {/* <!-- partial --> */}
            <div className="container-fluid page-body-wrapper">
              {/* <!-- partial:partials/_sidebar.html --> */}
              <NavbarA />
              {/* <!-- partial --> */}
              <div className="main-panel">
                <Switch>
                  <Route
                    path={"/category"}
                    render={(props) => (
                      <Category
                        {...props}
                        getCategory={this.getCategory}
                        category={this.state.category}
                        // user={this.state.user}
                      />
                    )}
                  />
                  <Route
                    exact
                    path={"/admin"}
                    render={(props) => (
                      <Dashboard
                        {...props}
                        // getCategory={this.getCategory}
                        // getusers={this.getusers}
                        // getpublic={this.getpublic}
                        // getorders={this.getorders}
                        // getfundrisers={this.getfundrisers}
                        // publics={this.state.publics}
                        // category={this.state.category}
                        // users={this.state.users}
                        // orders={this.state.orders}
                        fundrisers={this.state.fundrisers}
                      />
                    )}
                  />
                </Switch>{" "}
                {/* <!-- main-panel ends --> */}
                <footer className="footer">
                  <div className="d-sm-flex justify-content-center justify-content-sm-between">
                    <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                      Copyright © 2020{" "}
                      {/* <Link to="https://www.bootstrapdash.com/" target="_blank">
                        BootstrapDash
                      </Link> */}
                      . All rights reserved.
                    </span>
                    <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                      Hand-crafted &amp; made with{" "}
                      <i className="mdi mdi-heart text-danger"></i>
                    </span>
                  </div>
                </footer>
              </div>
            </div>
            {/* <!-- page-body-wrapper ends --> */}
          </div>
        </div>
      </Router>
    );
  }
}
