import React, { Component } from "react";
import "../CSS/donor.css";

import axios from "axios";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      category: [],
      name: "",
      cat: "",
      phone: "",
      email: "",
      address: "",
      hospitalname:"",
      doctorname:"",
      doctorcontact:"",
      aadhar:"",
      bankaccountnumber:"",
      goalamount:"",
       file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        category: res.data.data,
      });
      console.log(this.state.category);
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  // Input on change
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  // Dropdown change
  handleDropdownChange = (e) => {
    this.setState({ cat: e.target.value });
    console.log(e.target.value);
  };
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
    console.log(this.state.file);
  };
  onSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("file", this.state.file, this.state.file.name);

    console.log(data);
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    };
    try {
      const res = await axios.post(
        `http://localhost:5000/api/v1/fundriser/photo`,
        data,
        config
      );
      console.log(res.data.data);

      const fundriser = {
        name: this.state.name,
        category: this.state.category,
        phone: this.state.phone,
        email: this.state.email,
        address: this.state.address,
        hospitalname: this.state.hospitalname,
        doctorname: this.state.doctorname,
        doctorcontact: this.state.doctorcontact,
        aadhar: this.state.aadhar,
        bankaccountnumber: this.state.bankaccountnumber,
        goalamount: this.state.goalamount,
        photo: res.data.data,
      };
      const body = JSON.stringify( fundriser);
      console.log(body);
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.post(
        `http://localhost:5000/api/v1/ fundriser`,
        body,
        config1
      );
      console.log(result);
      alert(`Doctors Details Added ${result.data.data.name}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };

  render() {
    return (
      <div className="container itmtop">
         {console.log(this.state)}
        <div className="">
          {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
          <div className="" id="login-second">
            <div class="page-wrapper p-t-50 p-b-50">
              <div class="wrapper wrapper--w900 ">
                <div class="card cardH card-6 ">
                  <div class="card-heading m-4">
                    <h2 class="title text-dark">Add Information</h2>
                  </div>
                  <div class="card-body">
                    <form onSubmit={this.onSubmit}  encType="multipart/form-data">
                      <div class="form-row frow">
                        <div class="name">name:</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="text"
                            name="name"
                            value={this.state.name}
                            onChange={this.onChange}
                          />
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Specialization</div>
                        <div className="value">
                          <select
                            id="dropdown form-control"
                            className="btn bg-light"
                            onChange={this.handleDropdownChange}
                          >
                            <option value="nocat">Specialization</option>
                            {this.state.category.map((cat) => (
                              <option key={cat._id} value={cat._id}>
                                {cat.catname}
                              </option>
                            ))}
                          </select>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Phone:</div>
                        <div class="value">
                          <div class="input-group">
                          <input
                            className="input--style-6"
                            type="text"
                            name="phone"
                            value={this.state.phone}
                            onChange={this.onChange}
                          />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Email</div>
                        <div class="value">
                          <div class="input-group">
                          <input
                            className="input--style-6"
                            type="text"
                            name="email"
                            value={this.state.email}
                            onChange={this.onChange}
                          />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Address</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="address"
                              value={this.state.address}
                            onChange={this.onChange}
                          />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Hospital name:</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="hospitalname"
                              value={this.state.hospitalname}
                            onChange={this.onChange}

                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Doctor name:</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="doctorname"
                              value={this.state.doctorname}
                            onChange={this.onChange}

                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Doctor Contact</div>
                        <div class="value">
                          <div class="input-group">
                            <input
