import React, { Component } from "react";

import { BrowserRouter as Router, Route, Switch } from "./node_modules/react-router-dom";


// import fRegistration from "./Registration";
import DonorNavbar from "./DonorNavbar";
import DonorHome from "./DonorHome";
import Category from "./Category";

import axios from "axios";
import showItems from "./showItems";


export default class All extends Component {
  state = {
    users: [],
    doctors: [],
    category: [],
  };

  getCategory = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        category: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };

  getDoctors = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        ` http://localhost:5000/api/v1/doctor`,
        config
      );
      this.setState({
        doctors: res.data.data,
      });
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  render() {
    return (
      <Router>
        <div className="">
          <DonorNavbar />
          {/* <div className="jumbotron" style={{ marginBottom: 0 + "px" }}></div> */}

          <Switch>
            <Route
              exact
              path={"/donor/Home"}
              render={(props) => (
                <DonorHome
                  {...props}
                  user={this.state.user}
                  getDoctors={this.getDoctors}
                  // getCategory={this.getCategory}
                  doctors={this.state.doctors}
                  // category={this.state.category}
                />
              )}
            />
            {/* <Route exact path={"/user/Home"} component={CompanyHome} /> */}
            <Route
              exact
              path={"/donor/Category"}
              render={(props) => (
                <Category
                  user={this.state.user}
                  getDoctors={this.getDoctors}
                  getCategory={this.getCategory}
                  doctors={this.state.doctors}
                  category={this.state.category}
                />
              )}
            />

            <Route path={"/donor/Showitems"} component={showItems} />
          </Switch>
        </div>
      </Router>
    );
  }
}

// export default App;
